mapper.select:
SELECT /* fn: getFullCodeList, wr: shanepark, date: 2023-05-04 */ COMMON_CODE AS commonCode , COMMON_NAME AS commonName , PARENTS_CODE AS parentsCode FROM TBL_COMMON_CODE
AND USE_YN = #{useYN}
AND DEL_YN = #{delYN}
AND (PARENTS_CODE = 'PGM_CATEGORY' OR PARENTS_CODE LIKE 'PGM_CATE%' )
AND COMMON_CODE != 'PGM_CATEGORY' AND COMMON_CODE NOT LIKE '%PGM_CATE%' AND PARENTS_CODE != 'PGM_CATEGORY' AND PARENTS_CODE NOT LIKE '%PGM_CATE%'
AND GROUP_CODE = #{groupCode}
ORDER BY CODE_ORDER ASC

SELECT /* fn: getParentsCodeList, wr: shanepark, date: 2023-05-04 */ COMMON_CODE AS commonCode , COMMON_NAME AS commonName , PARENTS_CODE AS parentsCode , REFER_ID1 AS referId1 , REFER_ID2 AS referId2 FROM TBL_COMMON_CODE
AND USE_YN = #{useYN}
AND DEL_YN = #{delYN}
AND PARENTS_CODE = 'ROOT' AND GROUP_CODE = #{groupCode}
ORDER BY CODE_ORDER ASC

mapper.insert:
INSERT	/* fn: doInsertCode, wr: shanepark, date: 2023-05-04 */
INTO TBL_COMMON_CODE (
	COMMON_CODE					/*  */
	, COMMON_NAME 				/*  */
	, PARENTS_CODE				/*  */
	, GROUP_CODE				/*  */
	, COMMENT					/*  */
	, CODE_ORDER				/*  */
	, REG_USER					/*  */
	, REG_DATE					/*  */

		, REFER_ID1					/*  */


		, REFER_ID2					/*  */

) VALUES (
	#{commonCode}
	, #{commonName}
	, #{parentsCode}
	, #{groupCode}
	, #{comment}
	, #{codeOrder}
	, #{regUser}
	, NOW()

		, #{referId1}


		, #{referId2}

)

mapper.update:
UPDATE /* fn: doDeleteCode, wr: shanepark, date: 2023-05-04 */
TBL_COMMON_CODE SET 
DEL_YN = 'Y'
WHERE 	COMMON_CODE 		= #{commonCode} 
	AND PARENTS_CODE 		= #{parentsCode} 
	AND GROUP_CODE 			= #{groupCode}

mapper.select:
SELECT	/* fn: dupleCode, wr: shanepark, date: 2023-05-04 */
	COUNT(1)
FROM TBL_COMMON_CODE



		AND COMMON_CODE 		= #{commonCode} 



		AND GROUP_CODE 			= #{groupCode}

mapper.update:
UPDATE	/* fn: doUpdateCode, wr: shanepark, date: 2023-05-04 */
TBL_COMMON_CODE SET


			COMMON_NAME 			= #{commonName}
			, COMMENT				= #{comment}
			, CODE_ORDER			= #{codeOrder}
			, USE_YN				= #{useYN}

				, REFER_ID1				= #{referId1}


				, REFER_ID2				= #{referId2}



			USE_YN					= 'N'
			, DEL_YN				= 'Y'


	, MOD_USER					= #{modUser}
	, MOD_DATE					= NOW()
	WHERE COMMON_CODE 		= #{commonCode} 
		AND PARENTS_CODE 	= #{parentsCode} 
		AND GROUP_CODE 		= #{groupCode}

mapper.select:
SELECT	/* fn: getSubCodeList, wr: shanepark, date: 2023-05-04 */
	COMMON_CODE						AS commonCode
	, COMMON_NAME					AS commonName
	, PARENTS_CODE					AS parentsCode
	, REFER_ID1						AS referId1
	, REFER_ID2						AS referId2
	, COMMENT						AS comment
FROM TBL_COMMON_CODE


		AND USE_YN = #{useYN}


		AND DEL_YN = #{delYN}


		AND GROUP_CODE = #{groupCode}


		AND PARENTS_CODE = #{commonCode}


	ORDER BY CODE_ORDER ASC
